CREATE TABLE `listings` (`id` INT(11) NOT NULL AUTO_INCREMENT , `user_id` INT(11) NOT NULL , `title` VARCHAR(255) NOT NULL , `description` LONGTEXT NOT NULL , `price` VARCHAR(45) NOT NULL , `type` VARCHAR(10) NOT NULL , `address` VARCHAR(255) NOT NULL , `city` VARCHAR(45) NOT NULL , `state` VARCHAR(255) NOT NULL , `phone` VARCHAR(45) NOT NULL , `email` VARCHAR(45) NOT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `users` (`id` INT NOT NULL AUTO_INCREMENT , `name` VARCHAR(255) NOT NULL , `email` VARCHAR(255) NOT NULL , `password` VARCHAR(255) NOT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;
ALTER TABLE `listings` ADD CONSTRAINT `fk_listings_users` FOREIGN KEY (`user_id`) REFERENCES `users`(`id`) ON DELETE CASCADE ON UPDATE CASCADE;


INSERT INTO `listings` (`id`, `user_id`, `title`, `description`, `price`, `type`, `address`, `city`, `state`, `phone`, `email`, `created_at`) VALUES (NULL, '1', '3 bed semi-detached house for sale', 'This large semi-detached house is being offered for sale with no forward chain and features a separate lounge and dining room, good sized kitchen and large bedrooms. The property is ideally located within walking distance to several schools, Welling High Street and train station.This property is for sale by the Modern Method of Auction. Should you view, offer or bid on the property, your information will be shared with the Auctioneer, iamsold.', '450000', 'House', 'Danson Lane, Welling, Kent DA16', 'Kent', 'England', '02080 228456', 'john@gmail.com', CURRENT_TIMESTAMP), (NULL, '1', '2 bed flat for sale', 'This spacious 2 bedroom ground floor apartment would make an ideal first time buy/investment or downsize property. Ideally located in a sought after central Bexleyheath location within walking distance of Bexleyheath Broadway Shopping Centre and with excellent transport links. This property comprises of hall, open plan lounge, dining area and kitchen, 2 bedrooms with master en-suite and family bathroom. Also benefits from a large private terrace area and allocated parking.', '350000', 'Flat', 'Sterling Road, Bexleyheath DA7', 'Bexleyheath', 'England', '02080 228456', 'jon@gmail.com', CURRENT_TIMESTAMP);
INSERT INTO `listings` (`id`, `user_id`, `title`, `description`, `price`, `type`, `address`, `city`, `state`, `phone`, `email`, `created_at`) VALUES (NULL, '1', '3 bed semi-detached house for sale', 'John Mair & re/max Coast & Country are delighted to offer this immaculately presented modern three bedroom semi detached house in the Riverside Quarter of Aberdeen. The property is situated over two floors and has a lounge/dining room, kitchen, separate WC/Utility Room, family bathroom and 3 bedrooms. Access is via a front garden planted with assorted shrubs to a partially glazed Nordan security front door. Private driveway with space for 2 cars can be found to the side of the property. The property benefits from full uPVC double glazing and modern boiler providing gas central heating. The property is within the catchment area for both Stoneywood Primary School and Bucksburn Academy. It is in “move in” condition with 4 years builders warranty and would make a perfect home for young couples or families.', '214000', 'House', 'Mugiemoss Drive, Bucksburn, Aberdeen AB21', 'Aberdeen', 'Scotland', '348-334-3949', 'john@gmail.com', CURRENT_TIMESTAMP), (NULL, '1', '4 bed detached house for sale', 'Fantastic opportunity to purchase this well-presented four-bedroom detached villa located in a highly sought-after estate in Brightons, near Falkirk. This lovely family home is complemented with a Monoblock driveway, integral garage and private sunny rear garden. The property is located close to all local amenities and is within the catchment area of the highly regarded Wallacestone Primary School and Braes High School.', '289000', 'House', 'Standrigg Gardens, Brightons, Falkirk FK2', 'Falkirk', 'Scotland', '02080 228456', 'jon@gmail.com', CURRENT_TIMESTAMP);